            var urlParameters = '&noOfHistoryTerms=' + page.Blocs.Search.maxSearchHistory;
            var ajaxRemoveMethod = 'POST';
            var parameterType = '?';
            var virtualSiteIdentifier = 'virtualSiteId=';
