                                var $newItem = $item.clone();
                                var $itemSelector = $('<span>', {
                                    class: 'block-search-suggestion-result-selector',
                                    text: value
                                });
                                
                                $newItem
                                .append($itemSelector)
                                // .appendTo(me.$container)
                                .attr(_data_position_, i + 1)
